<template>
  <div class="tab-contents">
   

<wintabe ref="wintabe" :table="true" :router="true"></wintabe>

<div class=" flex   flex-pack-center">
       <div  style="width:60%;">
<div style="width:100%;background-color:#f7f7f7;height:47px;padding:0 10px;margin-bottom:10px;" class="flex  flex-align-center">
  选择支付方式
</div>
</div>
</div>

<div class=" flex   flex-pack-center ">

<div class="contentBox2 flex">

    <div style="height:90px;margin:10px;border:1px  solid #fff;" class="flex" :style="payActive == 'ali'?'border:1px  solid #F4C542':''" @click="payActive = 'ali'" v-if="goodsType == 'RETAIL'">
        <img style="cursor: pointer;" :src="`data:image/png;base64,`" />

    </div>
    <div style="margin:10px;height:90px;border:1px  solid #fff;" class="flex" :style="payActive == 'wechat'?'border:1px  solid #F4C542':''" @click="payActive = 'wechat'" v-if="goodsType == 'RETAIL'">
        <img style="cursor: pointer;" src="data:image/png;base64," />
    </div>


<div style="margin:10px;height:90px;border:1px  solid #fff;" class="flex" :style="payActive == 'SCORE'?'border:1px  solid #F4C542':''" @click="payActive = 'SCORE'" v-if="goodsType == 'SCORE'">

<img v-if="awardBalance>= obj.payTotal" style="cursor: pointer;" src="data:image/png;base64,"/>  
<img v-else src="data:image/png;base64,"/>
</div>
<div  class=" flex  flex-align-center" v-if="goodsType == 'SCORE'" style="color:#ACACAC">
  <span>积分余额{{awardBalance.toFixed(2)}} {{balanceDown()?'，积分不足':''}}</span>
  <!-- {{`积分余额${awardBalance.toFixed(2)}${awardBalance<obj.payTotal?'，积分不足':''}`}} -->
</div>
</div>
</div>


<div class=" flex   flex-pack-center ">
<div class="btmContent flex flex-pack-justify" style="padding:20px;font-size:14px;text-align:right">
<div style="color:#ACACAC">
<span v-if="goodsType == 'SCORE'">注意：积分商品不支持退款服务，请确定需要商品再完成支付。</span>
</div>
    <div>

      <span v-if="goodsType == 'SCORE'">支付积分：<span style="font-size:16px;color:red">{{obj.payTotal?obj.payTotal.toFixed(2):obj.payTotal}}积分</span></span>
      <span v-else>实付总额：<span style="font-size:16px;color:red">￥{{obj.payTotal?obj.payTotal.toFixed(2):obj.payTotal}}</span></span>

      </div>
</div>
</div>
<div class=" flex   flex-pack-center ">
  <div style="width:60%;">
     <div style="float:right;">

  

     <div   :style=" `${surplus.minutes==0 && surplus.seconds ==0?'background-color:#999;color:#FFFFFF;':'background-color:#F4C542;color:#FFFFFF;'}${  balanceDown() && goodsType == 'SCORE'?'border-color: #D1D1D1;background-color: #D1D1D1;':''}`" style="border-color:#F4C542; margin-top:20px;min-width:120px;height:42px;font-size:16px;" class="flex flex-align-center flex-pack-center pointer" 
  @click="dopay">
    <span>{{ surplus.minutes==0 && surplus.seconds ==0?'订单失效':  '立即支付'}}</span>
  </div>
     <div style="margin-top:5px;color:#666;font-size:12.8px;" :style="balanceDown() && goodsType == 'SCORE' ? 'display:none;':'' ">
    剩余付款时间:<span style="color:red">
  {{surplus.minutes}}分{{surplus.seconds}}秒
</span>
  </div>


  </div>
  </div>
</div>
<div v-html="orderString"></div>

  <div style=" position: relative;" @click="overPay()" v-if="overModel">
    <div style="background-color:rgba(0, 0, 0, 0.5);  z-index: 99999;position: fixed;width: 100%;height: 100vh;top:0;left:0;" >
      <div class="flex flex-pack-center flex-align-center" style="height:100vh;">
        <div class="flex flex-around-justify flex-align-center flex-v" style="background-color:#fff;width:550px;padding:20px;position: relative;">
    
    <div style="text-align:left;" class="flex flex-align-center">
    <img style="width:100px;margin-right:10px;" src="data:image/png;base64,"/>
    <span>支付{{obj.payTotal}}元</span>
</div>
<div v-if="payStatus == 'qrcode'">
    <qriously :value="mwebUrl" :size="200"></qriously>
</div>
<div style="text-align: center;    margin: 20px;" v-if="payStatus == 'paying'">
  <i class="iconfont icon-shouji" style="    font-size: 50px;">  </i>
  <div style="    ">请在手机上进行支付</div>
  <div style="color:#2a69b6;"><span style="cursor: pointer;" @click="payStatus = 'qrcode'">返回二维码</span></div>
</div>
    <img src="data:image/png;base64," style="width:200px;"/>
          </div>
        </div>
      </div>
    </div>
  <winbeet></winbeet>
  </div>
</template>

<script lang="ts">
import Vue from "vue";
import Component from "vue-class-component";
import mixin from "../../config/mixin";
import { Action } from "vuex-class";
import { Toast } from "vant";
// import { recommendList } from '../../service/getData';
import Wintabe from "../../components/Wintabe.vue";
import Winbeet from "../../components/Winbeet.vue";
@Component({
  components: {
    Wintabe,
    Winbeet
  },
  mixins: [mixin]
})
export default class shopIndex extends Vue {
  obj: any = { body: "", payId: "", payTotal: "" };
  address = { contactname: "", contactmobile: "", address: "" };
  payActive = "ali";
  mwebUrl = "";
  overModel = false;

  payStatus = "qrcode";
  orderString = "";
  overPay() {
    this.overModel = false;
    clearInterval(this.timer);
  }

  dopay() {
    if (this.surplus.minutes == 0 && this.surplus.seconds == 0) {
      return;
    }

    if (this.balanceDown() && this.goodsType == "SCORE") {
      return;
    }
    //    this.obj["payTotal"]
    if (this.payActive == "ali") {
      Vue.prototype.$reqFormPost(
        "/ali/pay/wap",
        {
          userId: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .userId,
          token: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .token,
          clientType: "WEB",
          body: this.obj["body"],
          outTradeNo: this.obj["payId"],
          totalFee: this.obj["payTotal"]
        },
        res => {
          if (res == null) {
            console.log("网络请求错误！");
            return;
          }
          if (res.data.status != 200) {
            console.log(
              "需控制错误码" +
                res.data.status +
                ",错误信息：" +
                res.data.message
            );
            Toast(res.data.message);
            return;
          }
          this.orderString = res.data.data.orderString;
          this.$nextTick(() => {
            (<any>document.forms[0]).submit();
          });

          // window.location.href =
          //   "https://openapi.alipay.com/gateway.do?" +
          //   res.data.data.orderString;
        }
      );
    }
    if (this.payActive == "wechat") {
      //   window.location.href ="https://open.weixin.qq.com/connect/oauth2/authorize?appid=wx2e2d97a4e10ef2b6&redirect_uri=http://sr.cncloud.com/qichang/wechat/enter/call?action=viewtest&response_type=code&scope=snsapi_userinfo&state="+
      //  this.obj["payId"] +"#wechat_redirect"
      let a: any = window;
      Vue.prototype.$reqFormPost(
        "/wechat/pay/scan",
        {
          userId: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .userId,
          token: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .token,
          spbillCreateIp: a.getAddressIP().cip,
          body: this.obj["body"],
          outTradeNo: this.obj["payId"],
          totalFee: this.obj["payTotal"]
        },
        res => {
          if (res == null) {
            console.log("网络请求错误！");
            return;
          }
          if (res.data.status != 200) {
            console.log(
              "需控制错误码" +
                res.data.status +
                ",错误信息：" +
                res.data.message
            );
            this["$Message"].warning(res.data.message);

            return;
          }
          if (res.data.data.codeUrl) {
            this.overModel = true;
            this.mwebUrl = res.data.data.codeUrl;
            this.lveventTime();
          } else {
            this["$Message"].warning("获取二维码失败");
          }
        }
      );
    }

    if (this.payActive === "SCORE") {
      Vue.prototype.$reqFormPost(
        "/order/score/pay",
        {
          userId: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .userId,
          token: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .token,
          payId: this.obj["payId"],
          payTotal: this.obj["payTotal"]
        },
        res => {
          if (res == null) {
            console.log("网络请求错误！");
            return;
          }
          if (res.data.status != 200) {
            console.log(
              "需控制错误码" +
                res.data.status +
                ",错误信息：" +
                res.data.message
            );
            this["$Message"].warning(res.data.message);

            return;
          }

          this.$router.replace({
            name: "orderlist",
            query: {
              orderStatus: "ORDER_WAIT_SENDGOODS"
            }
          });
        }
      );
    }
  }

  destroyed() {
    clearInterval(this.timer);
  }
  timer: any = {};
  lveventTime() {
    this.timer = setInterval(() => {
      this.checkPayStatus();
    }, 4000);
  }

  checkPayStatus() {
    Vue.prototype.$reqFormPost(
      "/wechat/order/status",
      {
        userId: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
          .userId,
        token: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
          .token,
        outTradeNo: this.obj["payId"]
      },
      res => {
        if (res == null) {
          console.log("网络请求错误！");
          return;
        }
        if (res.data.data.tradeState == "USERPAYING") {
          this.payStatus = "paying";
        } else if (res.data.data.tradeState == "SUCCESS") {
          this.$router.replace({
            path: "/orderlist",
            query: {
              orderStatus: "ORDER_WAIT_SENDGOODS"
            }
          });
          this["$Message"].success(res.data.message);
        }
      }
    );
  }

  handlePX(CssName, PxNumber) {
    return (
      CssName +
      ":" +
      this.$store.getters[Vue.prototype.MutationTreeType.SYSTEM].availWidth /
        750 *
        PxNumber +
      "px;"
    );
  }
  createTime = null;

  goodsType = null;
  awardBalance = 0;

  balanceDown() {
    return this.awardBalance < this.obj.payTotal;
  }
  getreward() {
    Vue.prototype.$reqFormPost(
      "/user/account/query",
      {
        userId: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
          .userId,
        token: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
          .token
      },
      res => {
        if (res == null) {
          console.log("网络请求错误！");
          return;
        }
        if (res.data.status != 200) {
          console.log(
            "需控制错误码" + res.data.status + ",错误信息：" + res.data.message
          );
          Toast(res.data.message);
          return;
        }
        this.awardBalance = res.data.data.awardBalance;
        // this.awardBalance = 0;
      }
    );
  }
  mounted() {
    this.createTime = this.$route.query.createTime;
    this.obj.body = this.$route.query.body;
    this.obj.payId = this.$route.query.payId;
    this.obj.payTotal = this.$route.query.payTotal;
    this.address.address = this.$route.query.address;
    this.address.contactname = this.$route.query.contactname;
    this.address.contactmobile = this.$route.query.contactmobile;
    this.goodsType = this.$route.query.goodsType;

    if (this.goodsType === "SCORE") {
      this.payActive = "SCORE";
      this.getreward();
    }

    if (this.$route.query.orderId) {
      Vue.prototype.$reqFormPost(
        "/order/residue/time",
        {
          userId: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .userId,
          token: this.$store.getters[Vue.prototype.MutationTreeType.TOKEN_INFO]
            .token,
          orderId: this.$route.query.orderId
        },
        res => {
          if (res == null) {
            console.log("网络请求错误！");
            return;
          }
          this.residueTime = res.data.data.residueTime;
          this.handleResidueTime(this.residueTime);
          let timer = setInterval(() => {
            if (this.surplus.minutes == 0 && this.surplus.seconds == 0) {
              clearInterval(timer);
            } else {
              this.residueTime -= 1;
              this.handleResidueTime(this.residueTime);
            }
          }, 1000);
        }
      );

      return;
    }

    if (this.createTime) {
      let a = this.createTime;
      this.timeFn(a);
      let timer = setInterval(() => {
        if (this.surplus.minutes == 0 && this.surplus.seconds == 0) {
          clearInterval(timer);
        } else {
          this.timeFn(a);
        }
      }, 1000);
    }
  }
  residueTime = 0;

  handleResidueTime(residueTime) {
    this.surplus.minutes = parseInt((residueTime / 60).toString());
    this.surplus.seconds = residueTime % 60;
  }

  // 剩余时间
  surplus = {
    minutes: 0,
    seconds: 0
  };
  a_minutes = 0;
  a_seconds = 0;

  /**
   * 结算页面进来 计时与服务器的时间差
   * 订单那边进来 使用的是接口给的秒数
   */
  timeFn(d1) {
    let dateBegin: any = new Date(d1.replace(/-/g, "/"));
    let a = new Date().getTime();
    parseInt(((a - dateBegin.getTime()) / 1000).toString()) < 0 &&
    this.a_seconds == 0
      ? (this.a_seconds = parseInt(
          ((a - dateBegin.getTime()) / 1000).toString()
        ))
      : "";
    dateBegin.setSeconds(dateBegin.getSeconds() + 1800 + this.a_seconds);
    var dateDiff: any = parseInt(((dateBegin.getTime() - a) / 1000).toString()); //时间差的毫秒数
    this.residueTime = dateDiff;

    this.handleResidueTime(this.residueTime);
  }
}
</script>

